    ["Authentication.TwoStepVerification.Description.TwoStepVerificationGenericEmail.V1.Footer"] = [[Vui lòng không trả lời trực tiếp email này. Bạn nhận được email này vì chúng tôi cần Xác minh 2 bước để hoàn tất hành động mà bạn muốn thực hiện.]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationGenericEmail.V1.Body"] = [[{paragraphStart}Xin chào,{paragraphEnd}{paragraphStart}{emphasisStart}{code}{emphasisEnd} là mã Xác minh 2 bước Roblox cho {emphasisStart}{accountName}{emphasisEnd}.{paragraphEnd}{paragraphStart}Hãy nhập mã trên vào màn hình Xác minh 2 bước để tiếp tục hành động bạn muốn thực hiện. Mã sẽ hết hạn sau 15 phút.{paragraphEnd}{paragraphStart}Yêu cầu này được nhận từ {emphasisStart}{geoLocationInformation}{emphasisEnd}.{paragraphEnd}{paragraphStart}QUAN TRỌNG: Không chia sẻ mã bảo mật với bất kỳ ai. Roblox sẽ không bao giờ hỏi mã của bạn. Việc chia sẻ này bao gồm các hành động như nhắn tin mã của bạn, chia sẻ màn hình, v.v. Khi chia sẻ mã bảo mật cho người khác, bạn đặt tài khoản và nội dung trong tài khoản của mình vào tình trạng rủi ro cao.{paragraphEnd}{lineBreak}{paragraphStart}Cảm ơn,{lineBreak}{emphasisStart}Đội ngũ Roblox{emphasisEnd}{paragraphEnd}]],
    ["Authentication.TwoStepVerification.Label.SecurityKeyDirections"] = [[Khi thông báo hiện lên, hãy chạm vào khóa của bạn hoặc sử dụng sinh trắc học trên thiết bị này.]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationActivationSecurityKey.Body.Over13"] = [[Xin chào {accountName},{lineBreak}{lineBreak}Bạn đã kích hoạt Xác minh 2 bước bằng Khóa bảo mật cho tài khoản Roblox của mình. Đối với một số hành động như đăng nhập, bạn sẽ cần dùng khóa bảo mật.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationActivationSecurityKey.EmailSubject"] = [[Đã kích hoạt Xác minh 2 bước bằng Khóa bảo mật cho tài khoản Roblox: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationActivationSecurityKey.Body.Under13"] = [[Xin chào,{lineBreak}{lineBreak}Xác minh 2 bước bằng Khóa bảo mật đã được kích hoạt cho tài khoản Roblox của con bạn, {accountName}. Đối với một số hành động như đăng nhập, con bạn sẽ cần dùng khóa bảo mật.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationCustomerSupportDeactivationSecurityKey.Subject"] = [[Hỗ trợ Roblox đã hủy kích hoạt Xác minh 2 bước bằng Khóa bảo mật cho tài khoản: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationCustomerSupportDeactivationSecurityKey.HtmlBody.Over13"] = [[Chào {accountName},{lineBreak}{lineBreak}Tin nhắn này là để thông báo cho bạn biết rằng đội ngũ hỗ trợ của chúng tôi đã hủy kích hoạt Xác minh 2 bước bằng Khóa bảo mật cho tài khoản Roblox của bạn. Việc hủy kích hoạt này là do yêu cầu hỗ trợ mà bạn đã gửi hoặc là một biện pháp bảo mật để đảm bảo an toàn cho tài khoản của bạn. Các hành động như đăng nhập sẽ không yêu cầu khóa bảo mật nữa. Bạn có thể kích hoạt lại Xác minh 2 bước trong {aTagStartWithHref}{AccountSettingsPageLink}{hrefEnd}cài đặt tài khoản{aTagEnd} của mình.{lineBreak}{lineBreak}Nếu bạn cần thêm hỗ trợ, {spanStartTagWithBold}xin đừng trả lời email này{spanEndTag}, hãy phản hồi yêu cầu hỗ trợ hiện tại của bạn hoặc gửi yêu cầu hỗ trợ mới tại đây: {aTagStartWithHref}{supportPageLink}{hrefEnd}Hỗ trợ Roblox{aTagEnd}.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationCustomerSupportDeactivationSecurityKey.HtmlBody.Under13"] = [[Xin chào,{lineBreak}{lineBreak}Tin nhắn này là để thông báo cho bạn biết rằng đội ngũ hỗ trợ của chúng tôi đã hủy kích hoạt Xác minh 2 bước bằng Khóa bảo mật cho tài khoản Roblox của con bạn, {accountName}. Việc hủy kích hoạt này là do yêu cầu hỗ trợ mà bạn đã gửi hoặc là một biện pháp bảo mật để đảm bảo an toàn cho tài khoản của bạn. Các hành động như đăng nhập sẽ không yêu cầu khóa bảo mật nữa. Bạn có thể kích hoạt lại Xác minh 2 bước trong {aTagStartWithHref}{AccountSettingsPageLink}{hrefEnd}cài đặt tài khoản{aTagEnd}.{lineBreak}{lineBreak}Nếu bạn cần thêm hỗ trợ, {spanStartTagWithBold}xin đừng trả lời email này{spanEndTag}, hãy phản hồi yêu cầu hỗ trợ hiện tại của bạn hoặc gửi yêu cầu hỗ trợ mới tại đây: {aTagStartWithHref}{supportPageLink}{hrefEnd}Hỗ trợ Roblox{aTagEnd}.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationDeactivationSecurityKey.Subject"] = [[Đã hủy kích hoạt Xác minh 2 bước bằng Khóa bảo mật cho tài khoản Roblox: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationDeactivationSecurityKey.Body.Over13"] = [[Xin chào {accountName},{lineBreak}{lineBreak}Bạn đã hủy kích hoạt Xác minh 2 bước bằng Khóa bảo mật cho tài khoản Roblox của mình.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationDeactivationSecurityKey.Body.Under13"] = [[Xin chào,{lineBreak}{lineBreak}Xác minh 2 bước bằng Khóa bảo mật đã được hủy kích hoạt cho tài khoản Roblox của con bạn, {accountName}.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.SecurityWarningShort"] = [[{boldStart}QUAN TRỌNG:{boldEnd} Không chia sẻ mã bảo mật với bất kỳ ai. Roblox sẽ không bao giờ hỏi mã của bạn. Việc chia sẻ này bao gồm các hành động như nhắn tin mã của bạn, chia sẻ màn hình, v.v.]],
